import React from 'react'
import EliminarUsuario from './EliminarUsuario'
import lapiz from '../images/pencil-edit.svg'

export class TablaUsuarios extends React.Component {
    render(){
        const usuarios = this.props.usuarios;
        const filaUsuarios = usuarios.map(user => {
            return (
                <tr key={user.dni} >
                    <td>{user.dni}</td>
                    <td>{user.nombre}</td>
                    <td>{user.apellido}</td>
                    <td>{user.rol}</td>
                    <td>{user.celular}</td>
                    <td>{user.email}</td>
                    <td>{user.ingreso}</td>
                    <td className="btnAgregarEliminar">
                        <button className="editarUsuario" type="button" onClick={() => this.props.onEditStudent(user)}><img src={lapiz} alt="lapiz" width="15px"/></button>
                        <button className="eliminarUsuario" ><img src={reciclaje} alt="basura" width="15px"/></button>
                        <EliminarUsuario/>
                        {/* <button type="button" onClick={() => this.props.onEditStudent(user)}>editar</button>
                        <button type="button" onClick={() => this.props.onDeleteStudent(user._id)}>eliminar</button> */}
                    </td>
                </tr> 
            );
        });

        /* === === === === === === === === === === === === === */
        return (
                <table id="tablaUsuarios">
                    <thead className="textCenter">
                        <tr>
                            <th>ID</th>
                            <th>Nombre</th>
                            <th>Apellido</th>
                            <th>Rol</th>
                            <th>Celular</th>
                            <th>E-mail</th>
                            <th>Ingreso</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody id="tbodyUsuarios">
                        {filaUsuarios}
                    </tbody>
                </table>
        );
    }
    
}
    
    

export default TablaUsuarios
