import './styles/App.css';
import React from 'react';

// importar mis componentes
import BtnNuevoUsuario from './components/BtnNuevoUsuario'
import TablaUsuarios from './components/TablaUsuarios'
import Cabezera from './components/Cabezera'
import BarraBusqueda from './components/BarraBusqueda';
import Footer from './components/Footer'
import axios from 'axios';

class App extends React.Component {
  constructor(props){
    super(props);
    this.URL_datosUsuarios = "http://localhost:5000/usuarios"; /* mis datos en JSON*/
    const emptyUsuario = { _id: -1 , dni: '', nombre: '', apellido: '', rol: '', especialidad: '', celular: '', email: '' , ingreso: ''};

    this.state = {
      Usuarios: [],
      selectedUsuario: this.emptyUsuario,
    };

    onFormChange = () => {
      console.log('cambio el formulario');
    };

    this.onFormChange = this.onFormChange.bind(this);
    this.onEditUser = this.onEditUser.bind(this);
    this.onDeleteUser = this.onDeleteUser.bind(this);
    this.onClearUser = this.onClearUser.bind(this);
    this.onSaveUser = this.onSaveUser.bind(this);

    ComponentDidMount = () => {
      axios.get(this.URL_datosUsuarios)
      .then(response => {console.log('Esta es listar estudiantes',response);
        this.setState({usuarios: response.data})
      }).catch(error => {
        console.log('hubo un error',error);
      })
    }

    onFormChange(userCurrentState); {
      console.log('cambió el formulario', userCurrentState);
      this.setState({ selectedUsuario: userCurrentState });
    }

    onEditStudent(usuario); {

      console.log('quiero editar un estudiante', usuario);
      this.setState({ selectedUsuario: usuario });

    }

    onDeleteStudent(usuarioId); {
      console.log('quiero eliminar un estudiante', usuarioId);
      axios.delete(`${this.URL_datosUsuarios}/${usuarioId}`).then(data => {
        this.setState((state, props) => ({
          usuarios: this.state.usuarios.filter(st => st._id !== usuarioId),
          selectedUsuario: this.emptyUsuario
        }))
      }).catch(err => {
        console.log('hubo error borrando');
      });
    }

    onClearStudent(); {
      console.log('vamos a limpiar el estudiante');
      this.setState({ selectedUsuario: this.emptyUsuario });
    }

    onSaveStudent(evt); {
      evt.preventDefault();
      const student = this.state.selectedUsuario;
      if (student.nombre === '' || student.dni === '' || student.apellido === '' || student.rol === '') {
        alert('Hay errores en el formulario');
        return;
      }
      if (this.state.selectedUsuario._id === -1) {
        console.log('vamos a hacer un POST', this.state.selectedUsuario);
        axios.post(this.STUDENTS_URL, { ...student, _id: null }).then((resp) => {
          console.log('Todo bien con el post', resp);
          this.setState((state, props) => ({
            students: [...state.students, resp.data],
            selectedUsuario: this.emptyUsuario
          }))
        }).catch(err => {
          console.log('error al hacer post', err);
        });
      } else {
        console.log('vamos a hacer un PUT', this.state.selectedUsuario);
        axios.put(`${this.STUDENTS_URL}/${student._id}`, { ...student }).then((resp) => {
          console.log('Todo bien con el put', resp);
          this.setState((state, props) => ({
            students: state.students.map(st => st._id === student._id ? student : st),
            selectedUsuario: this.emptyUsuario
          }))
        }).catch(err => {
          console.log('error al hacer post', err);
        });
      }
    }
  }
  render() {
    return (
      <body className="appBody">
        <header>
          <Cabezera />
        </header>
        <div id="contenedor">
          <h1 style={{ textAlign: 'center' }}>GESTION DE USUARIOS Y ROLES DEL SISTEMA</h1>
          <div id="contenedorBTN">
            <BtnNuevoUsuario />
            <BarraBusqueda />
            <h3 id="gu">Gestion Usuarios</h3>
          </div>
          <div>
            <TablaUsuarios URL_datosUsuarios={URL_datosUsuarios} />
          </div>
        </div>
        <Footer />

      </body>
    );
  }
}

export default App;
