import './styles/App.css';
import React from 'react';

// importar mis componentes
import BtnNuevoUsuario from './components/BtnNuevoUsuario'
import TablaUsuarios from './components/TablaUsuarios'
import Cabezera from './components/Cabezera'
import BarraBusqueda from './components/BarraBusqueda';
import Footer from './components/Footer'
import axios from 'axios';

class App extends React.Component {
  constructor(props){
    this.URL_datosUsuarios = "http://localhost:5000/usuarios"; /* mis datos en JSON*/
    const emptyUsuario = { _id: -1 , dni: '', nombre: '', apellido: '', rol: '', especialidad: '', celular: '', email: '' , ingreso: ''};

    this.state = {
      Usuarios: [],
      selectedUsuario: this.emptyUsuario,
    };

    onFormChange = () => {
      console.log('cambio el formulario');
    };

    this.onFormChange = this.onFormChange.bind(this);
    this.onEditUser = this.onEditUser.bind(this);
    this.onDeleteUser = this.onDeleteUser.bind(this);
    this.onClearUser = this.onClearUser.bind(this);
    this.onSaveUser = this.onSaveUser.bind(this);

    ComponentDidMount = () => {
      axios.get(this.URL_datosUsuarios)
      .then(response => {console.log('Esta es listar estudiantes',response);
        this.setState
      }).catch(error => {
        console.log('hubo un error',error);
      })
    }
  }
  render() {
    return (
      <body className="appBody">
        <header>
          <Cabezera />
        </header>
        <div id="contenedor">
          <h1 style={{ textAlign: 'center' }}>GESTION DE USUARIOS Y ROLES DEL SISTEMA</h1>
          <div id="contenedorBTN">
            <BtnNuevoUsuario />
            <BarraBusqueda />
            <h3 id="gu">Gestion Usuarios</h3>
          </div>
          <div>
            <TablaUsuarios URL_datosUsuarios={URL_datosUsuarios} />
          </div>
        </div>
        <Footer />

      </body>
    );
  }
}

export default App;
