import React from 'react'
import FormularioNuevoUsuario from './FormularioNuevoUsuario';
import "bootstrap/dist/css/bootstrap.css";

export class BtnNuevoUsuario extends React.Component {
    constructor(){
        
        state = {
            abrirContenido: false
        }
    }
    showFormNuevoUsuario = () => {
        if (this.state.abrirContenido === true) {
            this.setState({abrirContenido: false})
        }
        else {
            this.setState({abrirContenido: true})
        }
    }

    render() {
        return (
        <div>
            <div>
                <button onClick={this.showFormNuevoUsuario} id="btnNuevoUsuario" title="Nuevo Usuario">Nuevo Usuario</button>
            </div>
            
            <div id="fanu" hidden={!this.state.abrirContenido}>
                <p title="Cerrar" className="x" onClick={this.showFormNuevoUsuario} >&times;</p>
                    
                <FormularioNuevoUsuario 
                    // usuarios={this.state.selectedUsuario}
                    onFormChange={this.onFormChange}
                    onClearUser={this.onClearUser}
                    onSaveUser={this.onSaveUser}
                    onEditUser={this.onEditUser}
                    />
            </div>
        </div>
    )
    }
} 

export default BtnNuevoUsuario
